import React, { useState } from "react";
import "./css/DoctorLogin.css";
// Import your CSS file for styling
// import { Link } from "react-router-dom";
// import { useHistory } from "react-router-dom";
import { useNavigate} from "react-router-dom";
import DoctorDashboard from "./DoctorDashboard";
import { useEffect } from "react";

const DoctorLogin = () => {
  // const [email, setEmail] = useState("");
  const [userName, setUsername] = useState("");
  const [password, setPassword] = useState("");
  // const [emailError, setEmailError] = useState("");
  const [userError, setUserError] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userType, setUserType] = useState("doctor");
  const [doctorList, setDoctorList] = useState([]);
  const [id, setId] = useState(null);
  // const validateEmail = (email) => {
  //   console.log("in this validate email function");
  //   const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  //   return regex.test(email);
  // };
  const validateUsername = (userName) => {
    console.log("in the validation methjod of User name");
    // const user=username;
    return userName;
  };
  const navigate = useNavigate();
  useEffect(() => {
    const fetchDoctorList = async () => {
      try {
        const response = await fetch(
          "http://localhost:8080/Services/Health/getDoctorList"
        );

        if (!response.ok) {
          throw new Error("Failed to fetch");
        }

        const data = await response.json();
        setDoctorList(data); // Assuming the response is an array of doctor details
        console.log(data[0]);
        data.forEach((doctor, index) => {
          if (doctor.doctorName === userName) {
            setId(index + 1);
            console.log("the ID is: ", index + 1);
            console.log("the full name is: ", doctor.doctorName);
          }
        });
      
      } catch (error) {
        console.error("Error fetching doctor list:", error);
      }
    };


    fetchDoctorList();
  }, []); 


  const handleLogin = () => {
   
    if (!validateUsername(userName)) {
      setUserError("");
      setUserError("Pleas eprovide the correct user name");
      return;
    }
    if (userName && password) {
      setIsLoggedIn(true);
    }

    // Create an object with the user's credentials
    const userCredentials = {
      // email,
      userName,
      password,
      userType,
      id:1
    };
    

    const authenticateUser = async () => {
      try {
        const response = await fetch(
          "http://localhost:8080/Services/Health/AuthenticateUser",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(userCredentials),
          }
        );
        console.log("the user credentials are: ", userCredentials);
        if (!response.ok) {
          throw new Error("Login failed");
        }

        const data = await response.json();

        if (data.message === "Authentication Successful" && data.authCheck === true) {
          setIsLoggedIn(true);

          navigate("/DoctorDashboard", { state: { userName } }); // Passing userName to UserDashboard
        } else {
          setIsLoggedIn(false);
        }

        // Clear form fields or perform other actions after processing the response
        setUsername("");
        setPassword("");
      } catch (error) {
        // Handle errors, e.g., display an error message
        alert("INVALID CREDENTAILS");
      }
    };

    authenticateUser();
 
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>Login</h2>
        <form>
          <div className="form-group">
            <input
              // type="email"
              type="text"
              id="form3Example3"
              className={`form-control form-control-lg ${
                // emailError ? "is-invalid" : ""
                userError ? "is-invalid" : ""
              }`}
              placeholder="Enter a valid user name"
              // value={email}
              value={userName}
              // onChange={(e) => setEmail(e.target.value)}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <label htmlFor="username">Username</label>
            {setUserError && (
              <div className="invalid-feedback">{userError}</div>
            )}
          </div>
          <div className="form-group">
            <input
              type="password"
              id="form3Example4"
              className="form-control form-control-lg"
              placeholder="Enter password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <label htmlFor="password">Password</label>
            {/* <input type="password" id="password" name="password" /> */}
          </div>
          <button
            type="button"
            style={{ paddingLeft: "2.5rem", paddingRight: "2.5rem" }}
            onClick={handleLogin}>
            Login
          </button>
        </form>
      </div>
      <div className="login-image">
        <img
          src="https://as1.ftcdn.net/v2/jpg/02/58/30/24/1000_F_258302473_TN25oZ5Dt9MYHPEr5vAD62cUgRBSwNAG.jpg"
          alt="Login Image"
          // style={{ width: "500px", height: "200px", borderRadius: "10px" }}
        />
      </div>
    </div>
  );
};

export default DoctorLogin;
